// 初始化函数，设置收发缓冲区
static int8_t CDC_Init_FS(void)
{
  /* USER CODE BEGIN 3 */
  /* Set Application Buffers */
  USBD_CDC_SetTxBuffer(&amp;hUsbDeviceFS, UserTxBufferFS, 0);
  USBD_CDC_SetRxBuffer(&amp;hUsbDeviceFS, UserRxBufferFS);
  return (USBD_OK);
  /* USER CODE END 3 */
}


// CDC控制命令处理，具体处理过程需要用户自己编写。
// 其中包括串口参数的设置，要做串口转USB通信的话需要修改这里，只是为了用USB与PC通信则不用管这里。每个命令具体的意思需要查询CDC类手册。
static int8_t CDC_Control_FS(uint8_t cmd, uint8_t* pbuf, uint16_t length);


// 虚拟串口发送，发送时可直接调用
uint8_t CDC_Transmit_FS(uint8_t* Buf, uint16_t Len);


// 虚拟串口接收
// USB接收回调函数，在USB VPC接收到数据时，会进入该函数，在该函数中进行USB数据接收处理或进行函数回调即可。
static int8_t CDC_Receive_FS(uint8_t* Buf, uint32_t *Len);
